import React, { useState, useEffect } from "react";
import StudentDataCard from "./components/studentDataCard/StudentDataCard";
import ContentFilter from "./components/contentFilter/ContentFilter";
import styles from "./App.module.css";
//Chose to use modular css to make the css more obviously tied to the relevant components

function App() {

  const [studentDataInfo, studentDataSet] = useState([]);
  const [contentFilter, contentFilterSet] = useState([]);
  const [nameFilter, nameFilterSet] = useState([]);
  const [tagFilter, tagFilterSet] = useState([]);

  //api call
  async function fetchUrl(url) {
    const response = await fetch(url);
    const json = await response.json();
    let newStudentData = [];
    json.students.map(student => {
      let addTags = student;
      addTags.tags = [];
      newStudentData.push(addTags);
    });
    studentDataSet(newStudentData);
    contentFilterSet(newStudentData);
    nameFilterSet(newStudentData);
    tagFilterSet(newStudentData);
  }

  const addTag = (str, index) => {
    const tagForStudentData = [...studentDataInfo];
    tagForStudentData[index].tags.push(str);
    studentDataSet(tagForStudentData);
  };

  // filter functions for sorting content
  const nameFilterFunction = str => {
    let newNameFilter = [];
    studentDataInfo.map(student => {
      const fullName = `${student.firstName} ${student.lastName}`.toLowerCase();
      if (fullName.includes(str)) {
        newNameFilter.push(student);
      }
    });
    let contentFilter = [];
    tagFilter.map(student => {
      const fullName = `${student.firstName} ${student.lastName}`.toLowerCase();
      if (fullName.includes(str)) {
        contentFilter.push(student);
      }
    });
    contentFilterSet(contentFilter);
    nameFilterSet(newNameFilter);
  };

  const tagFilterFunction = str => {
    if (str) {
      let newTagFilter = [];
      let newContentFilter = [];

      studentDataInfo.map(student => {
        let tagged = false;
        student.tags.map(tag => {
          if (tag.includes(str)) {
            tagged = true;
          }
        });
        if (tagged) {
          newTagFilter.push(student);
        }
      });
      contentFilter.map(student => {
        let tagged = false;
        student.tags.map(tag => {
          if (tag.includes(str)) {
            tagged = true;
          }
        });
        if (tagged) {
          newContentFilter.push(student);
        }
      });
      contentFilterSet(newContentFilter);
      tagFilterSet(newTagFilter);
    } else {
      contentFilterSet(nameFilter);
      tagFilterSet(studentDataInfo);
    }
  };



  useEffect(() => {
    fetchUrl(`https://api.hatchways.io/assessment/students`); 
  }, []);
  return (
    <div className={styles.background}>
      <div className={styles.centralContainer}>
        <ContentFilter filterFunction={nameFilterFunction} type={`name`} />
        <ContentFilter filterFunction={tagFilterFunction} type={`tag`} />
        {contentFilter.map((student, index) => {
          function findAverage(array) {
            let sum = 0;
            for (let i = 0; i < array.length; i++) {
              sum += parseInt(array[i]);
            }
            let average = sum / array.length;
            return average;
          }
          const averageGrade = findAverage(student.grades);
          return (
            <StudentDataCard
              key={index.toString()}
              index={index}
              img={student.pic}
              firstName={student.firstName}
              lastName={student.lastName}
              email={student.email}
              company={student.company}
              skill={student.skill}
              grades={student.grades}
              averageGrade={averageGrade}
              tags={student.tags}
              addTag={addTag}
            />
          );
        })}
      </div>
    </div>
  );
}

export default App;
